<?php

namespace AppBundle\Service\PasswordEncoder;

use AppBundle\Service\PasswordEncoder\LegacyPasswordEncoder;
use Symfony\Component\PasswordHasher\Exception\InvalidPasswordException;

class LegacyPasswordEncoderTest extends \PHPUnit\Framework\TestCase
{
    public function testEncodingPasswordTooLong()
    {
        $this->expectException(InvalidPasswordException::class);

        $encoder = new LegacyPasswordEncoder();
        $encoder->hash('');
    }

    public function testEmptyPassword()
    {
        $encoder = new LegacyPasswordEncoder();
        $passwordEncoded = $encoder->hash('', null);

        $this->assertEquals('5030573512345671', $passwordEncoded);
    }

    public function testPassword()
    {
        $encoder = new LegacyPasswordEncoder();
        $passwordEncoded = $encoder->hash('password', null);

        $this->assertEquals('5d2e19393cc5ef67', $passwordEncoded);
    }

    public function testPasswordWithSalt()
    {
        $encoder = new LegacyPasswordEncoder();
        $passwordEncoded = $encoder->hash('password', 'salt');

        $this->assertEquals('5d2e19393cc5ef67', $passwordEncoded);
    }

    public function testInvalidPassword()
    {
        $encoder = new LegacyPasswordEncoder();
        $this->assertFalse($encoder->verify('5d2epasswordef67', 'password', null));
    }

    public function testValidPassword()
    {
        $encoder = new LegacyPasswordEncoder();
        $this->assertTrue($encoder->verify('5d2e19393cc5ef67', 'password', null));
    }

    public function testValidPasswordWithSalt()
    {
        $encoder = new LegacyPasswordEncoder();
        $this->assertTrue($encoder->verify('5d2e19393cc5ef67', 'password', 'salt'));
    }
}
